import React, { useMemo, useState, useEffect } from 'react';

const UseMemo = () => {
  const [add, setAdd] = useState(0);

  const memoizedValue = useMemo(() => {
    console.log('Calculating memoized value...');
    return add * 2;
  }, [add]); // Re-run the useMemo function only when the 'add' state changes

  // useEffect(() => {
  //   console.log('Memoized value changed:', memoizedValue);
  // }, [memoizedValue]); // Watch for changes in the memoized value and log it to the console

  useEffect(() => {
    console.log("Use Effect");
  }, [memoizedValue]);

  const handleClick = (a, b) => {
    console.log(a + b);
    setAdd(a + b);
  };

  return (
    <div>
      <h1>Use Memo</h1>
      <button onClick={() => handleClick(5, 5)}>Increment {add}</button>
      <p>Memoized Value: {memoizedValue}</p>
    </div>
  );
};

export default UseMemo;
