import React, { useState } from 'react';
import shortid from 'shortid';

const Todo = () => {
    const [inputValue, setInputValue] = useState('');
    const [todoList, setTodoList] = useState([]);
    const [getId, setGetId] = useState(null);
    const [formValid, setFormValid] = useState(fa);

    const handelOnChange = (event) => {
        setInputValue(event.target.value)
        // console.log(inputValue)
        if(inputValue.length === 0){
            setFormValid(false)  
        }
    }

    const handelDelete = (getId) => {
        const indexValue = todoList.findIndex((data)=>(data.id === getId))
        todoList.splice(indexValue, 1)
        setTodoList([...todoList])
        // console.log(todoList)
    }

    const handelEdit = (getId) => {
        const value = todoList.find((data)=>(data.id === getId))
        setInputValue(value.message)
        setGetId(getId)
        console.log(getId)
    }

    const handelSubmit = (event) => {
        event.preventDefault()
        if(!getId){
            setInputValue('')
            setTodoList([...todoList, {id:shortid.generate(), message: inputValue}])
        }
        else{

        }
    }

    return(
        <>
            <h1>Todo List</h1>
            <form>
                <input 
                type={'text'} 
                value={inputValue} 
                placeholder={'Add todo'} 
                onChange={handelOnChange}
                />
                <button onClick={handelSubmit}>Add todo</button>
            </form>

            {todoList?.length !== 0 ? (
            <ol>
                {
                   todoList?.map((value, index)=>(
                    <li key={value.id}>
                        <span>{value.message}</span>
                        <span>
                            <button onClick={()=>handelDelete(value.id)}>Delete</button>
                            {/* <button onClick={()=>handelEdit(value.id)}>Edit</button> */}
                        </span>
                    </li>
                   )) 
                }
            </ol>
            ) : (
                <h2>There is no data</h2>
            )}
        </>
    )

}

export default Todo
