import React, { useEffect, useState } from "react";
import { Route, Routes } from "react-router-dom";
import AuthScreen from "./Auth";
import ChangePassword from "./Auth/ChangePassword.js";
import Login from "./Auth/Login";
import Register from "./Auth/Register";
import DashboardHome from "./Components/DashboardHome";
import Employee from "./Components/Employee";
import EmployeeForm from "./Components/FormAddEdit";
import MyAccount from "./Components/MyAccount";
import Profile from "./Components/Profile";
import Dashboard from "./layout/Dashboard/Dashboard";
import NotFoundPage from "./layout/NotFoundPage";

function App() {
  const accessToken = localStorage.getItem('t_access_token');
  const [state, setState] = useState()
  const [isAuthenticated, setIsAuthenticated] = useState(!state)

  useEffect(() => {
    const isLoggedIn = !!accessToken;
    console.log("ðŸš€ ~ file: App.js:22 ~ useEffect ~ isLoggedIn:", isLoggedIn)
    setState(isLoggedIn)
  })

  useEffect(() => {
    if (isAuthenticated !== null) {
      setIsAuthenticated(isAuthenticated)
    } else {
      setIsAuthenticated(false)
    }
  }, [isAuthenticated])

  return (
    <>
      <Routes>
        {isAuthenticated ?
          (
            <Route path="/" element={<AuthScreen />}>
              <Route index element={<Login />} />
              <Route path="login" element={<Login />} />
              <Route path="register" element={<Register />} />
            </Route>
          ) : (
            <Route path="dashboard" element={<Dashboard />}>
              <Route index element={<DashboardHome />} />
              <Route path="dashboard" element={<DashboardHome />} />
              <Route path="employees" element={<Employee />} />
              <Route path="add-employee" element={<EmployeeForm />} />
              <Route path="profile" element={<Profile />} />
              <Route path="my-account" element={<MyAccount />} />
              <Route path="change-password" element={<ChangePassword />} />
            </Route>
          )
        }
        <Route path="*" element={<NotFoundPage />} />
      </Routes>
    </>
  );
}

export default App;











