/* eslint-disable array-callback-return */
import { Grid, IconButton, Stack, Typography } from '@mui/material'
import axios from 'axios';
import React, { useEffect, useState } from 'react'
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import ProfileCard from '../ProfileCard';
import { DialogBox } from '../DialogBox';

const Employee = () => {
  const [data, setData] = useState(null);
  const [deleteModelPop, setDeleteModelPop] = useState(false)
  const [modelData, setModelData] = useState()

  useEffect(() => {
    const apiUrl = 'http://localhost:3100/api/employee';
    const access_token = localStorage.getItem('access_token');
    const headers = {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${access_token}`,
    };
    axios.get(apiUrl, { headers })
      .then(res => {
        setData(res.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const deleteModel = (value) => {
    setDeleteModelPop(!deleteModelPop)
    setModelData(value)
  }

  const handleSubmitEmp = () => {
    console.log(modelData._id)
    axios.post(`${'http://localhost:3100/api/employee/delete'}`, `{"employeeID": "${modelData._id}"}`)
    .then(res => {
      // setDeleteModelPop(false)
      console.log(`"employeeID": "${modelData._id}"`)
    })
    .catch(err => console.log(err))
  }

  return (
    <div>
      <Typography variant="h5" component="h2">Employee Listing</Typography>
      <Grid container spacing={2} sx={{ mt: 1 }}>
        { (data?.response !== undefined) ?
          data?.response.map((value, index) => (
            <Grid item xs={2} key={value._id}>
              <ProfileCard data={value}>
                <Stack direction="row" spacing={1} style={{ position: "absolute", top: "10px", right: "10px"}}>
                  <IconButton aria-label="edit" color="primary" size="small" sx={{ ml: 2 }}>
                    <EditIcon fontSize="inherit" />
                  </IconButton>
                  <IconButton aria-label="delete" color="secondary" size="small" onClick={()=>deleteModel(value)}>
                    <DeleteIcon fontSize="inherit" />
                  </IconButton>
                </Stack>
              </ProfileCard>
            </Grid>
          )) : <Grid item xs={2}>Loading...</Grid>
        }
      </Grid>
      <DialogBox
      title={'You want to delete this employee?'}
      cancelBtn={'Close'}
      submitBtn="Yes"
      onSubmit={handleSubmitEmp}
      openModal={deleteModelPop} 
      closeModel={deleteModel}
      modelData={modelData?.name}
      />
    </div>
  )
}

export default Employee
