import React, { useState } from 'react'

const Form = () => {
    const [todo, setTodo] = useState('')
    const handleChange = (e) => {
        setTodo(e.target.value)
        // console.log(todo)
    }
    const handleSubmit = (e) => {
        e.preventDefault()
        setTodo('')
        // console.log({
        //     message: todo
        // })
    }

    return(
        <>
            <form onSubmit={handleSubmit}>
                <input type={"text"} placeholder={"Add a ToDo"} value={todo} onChange={handleChange}/>
            </form>
        </>
    )
}

export default Form
