import React, { useState } from 'react';
import shortid from 'shortid';

const Form = () => {
    const [inputValue, setInputValue] = useState('');
    const [todoList, setTodoList] = useState([]);
    const [formValid, setFormValid] = useState(false);
    // const [error, setError] = useState(false);

    const handelOnChange = (event) => {
        setInputValue(event.target.value)
        const countLatters = formValid
        setFormValid(...countLatters, formValid)
        // if(inputValue.length > 2){
        //     setFormValid(true)
        //     console.log(formValid)
        // }
    }

    const handelSubmit = (event) => {
        event.preventDefault()
        if(formValid){
            setInputValue('')
            setTodoList([...todoList, {id:shortid.generate(), message: inputValue}])
            setFormValid(false)
        }
        else {
        }
    }

    return(
        <>
            <form>
                <input 
                type={'text'} 
                value={inputValue} 
                placeholder={'Add todo'} 
                onChange={handelOnChange}
                />
                <button onClick={handelSubmit}>Add todo</button>
                <br />
                {formValid && <small>Please enter value</small>}
            </form>
        </>
    )
}

export default Form
