import React, { useRef } from 'react'
import AboutUsChild from './AboutUsChild';

const AboutUs = () => {

  const inputRef = useRef(null);

  const inputReff = useRef(null);

  const handleClick = () => {
    inputRef.current.value = "100";
    inputRef.current.style.color = "red";
    inputRef.current.focus()
  }

  const handleOnClick = () => {
    inputReff.current.value = "100";
    inputReff.current.focus()
    inputReff.current.style.backgroundColor = "red";
    inputReff.current.style.color = "white";
  }

  return (
    <div>
      <div>
        <AboutUsChild ref={inputRef} />
        <button onClick={handleClick}>Button</button>
      </div>
      <br />
      <br />
      <br />
      <div>
        <input type="text" ref={inputReff} />
        <button onClick={handleOnClick}>Button</button>
      </div>
    </div>
  )

}

export default AboutUs














