import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Authentication() {
    const navigate = useNavigate();

    const getToken = () => {
        const tokenString = localStorage.getItem(process.env.ACCESS_TOKEN)
        const userToken = tokenString && JSON.stringify(tokenString)
        return userToken;
    }

    const getUserDetail = () => {
        const userString = localStorage.getItem('user')
        const user_detail = JSON.stringify(userString)
        return user_detail;
    }

    const [token, setToken] = useState(getToken());
    const [user, setUser] = useState(getUserDetail());

    const saveToken = (token, user) => {
        localStorage.setItem(process.env.ACCESS_TOKEN, JSON.stringify(token))
        localStorage.setItem('user', JSON.stringify(user))

        setToken(token)
        setUser(user)
        navigate('/dashboard')
    }

    const logout = () => {
        localStorage.removeItem(process.env.ACCESS_TOKEN);
        localStorage.removeItem('user');
        navigate('/login')
    }

    const http = axios.create({
        baseURL: process.env.REACT_APP_BASE_URL,
        headers: {
            "Content-Type": "application/json"
        }
    })
    return {
        setToken: saveToken,
        token,
        user,
        getToken,
        http,
        logout
    }
}
