import React, { createContext } from "react";
import Footer from "./layout/footer";
import Header from "./layout/header";
import { BrowserRouter, Routes, Route, Outlet } from "react-router-dom";
import Login from "./Auth";
import Employee from "./Employee";
import Users from "./Users";
import AboutUs from "./AboutUs";

const title = createContext();
const subTitle = createContext();

function App() {
  const formTitle = "About Us Form";
  const formSubTitle = "This is useRef example."
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/" key="root" element={
          <>
            <Header/> 
              <div className='container'>
                  <main>
                    <Outlet /> 
                  </main>
              </div>
            <Footer />
          </>
        }>
          <Route path="/" element={<Employee />} />
          <Route path="/about-us" element={
          // <title.Provider value={formTitle}>
          //   <subTitle.Provider value={formSubTitle}>
              <AboutUs />
          //   </subTitle.Provider>
          // </title.Provider>
          } />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}

export default App;
export {title, subTitle};

















