import { Switch } from "@mui/material";
import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, Outlet, Router } from "react-router-dom";
import { Redirect } from "react-router-dom";
import Login from "./Auth";
import Dashboard from "./layout/Dashboard/Dashboard";

function App() {

  const isAuthenticated = /* your authentication logic here */ false;

  // useEffect(()=>{
  //   const access_token = localStorage.getItem('access_token');
  //   setIsLogged(Boolean(access_token))
  // })

  return (
    <>
      {/* <BrowserRouter>
        <Routes>
          <Route path="/" element={<Login />} />
          <Route path="/" key="root" element={<Outlet />} />
          {isAuthenticated && <Route path="/dashboard" element={<Dashboard />} />}
        </Routes>
      </BrowserRouter> */}
      <Router>
        <Switch>
          <Route path="/login" component={Login} />
          <PrivateRoute path="/dashboard" component={Dashboard} isAuthenticated={isAuthenticated} />
          <Redirect from="*" to="/login" />
        </Switch>
      </Router>
    </>
  );
}

// PrivateRoute component to handle redirection
const PrivateRoute = ({ component: Component, isAuthenticated, ...rest }) => (
  <Route
    {...rest}
    render={(props) =>
      isAuthenticated ? (
        <Component {...props} />
      ) : (
        <Redirect to={{ pathname: '/login', state: { from: props.location } }} />
      )
    }
  />
);

export default App;

















