import React, { Component } from 'react'
export default class App extends Component{
  constructor(){
    super();
    this.state = {
      counter: 0
    }
  }
  componentDidUpdate(prevProps, ){
    console.log("componentDidUpdate called")
  }
  render(){
    return(
      <>
        <h1>Component DidUpdate</h1>
        <button onClick={()=>this.setState({counter: this.state.counter+1})}>Click to Increase {this.state.counter}</button>
      </>
    )
  }
}



























