import React, { Component } from 'react'

class ComponentDidUpdate extends Component{

  constructor(){
    super();
    this.state = {
      counter: 0
    }
    console.log("constructor called")
  }

  componentDidMount(){
    document.title = `You clicked ${this.state.counter} times`;
    console.log("componentDidMount called")
  }

  componentDidUpdate(prevProps, prevState, snapShot){
    console.log("componentDidUpdate called", prevState)
    document.title = `You clicked ${this.state.counter} times`;
  }

  render(){
    console.log("render called")
    return(
      <>
        <h1>Component DidUpdate</h1>
        <Child />
        <button onClick={()=>this.setState({counter: this.state.counter+1})}>Click to Increase {this.state.counter}</button>
      </>
    )
  }
}

export default ComponentDidUpdate


class Child extends React.Component {
  componentWillUnmount(){
      console.log("Component is hidden now")
  }

  render() {
      return (
        <div>
          <h3>Child Component</h3>
        </div>
      )
  }
}
