import React, {useState} from 'react';

const Counter = () => {
    const [count, setCount] = useState(0);

    const increment = () => setCount({
        count: count + 1
    },
    () => {}
    )

    // const increment = () => {
    //     if(count < 10){
    //         setCount(count + 1);
    //     }
    //     console.log(count)
    // }

    const decrement = () => {
        if(count > 0){
            setCount(count - 1)
        }
        console.log(count)
    }

    return(
        <>
            <h1>Counter is: {count}</h1>
            <button disabled={(count === 0) ? true : false} onClick={decrement}>Decrement</button>
            &nbsp;&nbsp;
            <button disabled={(count === 10) ? true : false} onClick={increment}>Increment</button>
        </>
    )
}

export default Counter
