import React, { useState, useEffect } from 'react'
import { Button } from '@mui/material'
import { DialogBox } from '../genericComponent/DialogBox'
import EmployeeForm from './EmployeeForm'
// import shortid from 'shortid';


const cities = ['delhi', 'mumbai', 'banglore', 'kolkata', 'pune'];
const skills = ['angular', 'react', 'vue', 'nextjs', 'drupal', 'html5', 'css3', 'sass', 'mui', 'material-ui'];

const Employee = () => {
  const [addEmp, setAddEmp] = useState(false)
  const addEmployeeBtn = () => { setAddEmp(!addEmp) }


  const initialValues = {fullName: '', email: ''}
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit ] = useState(false)



  const handleChange = (e) => {
    // console.log(e.target)
    const {name, value} = e.target;
    setFormValues({ ...formValues, [name]: value })
    // console.log(formValues)
  }


  const handleSubmit = (e) =>{
    e.preventDefault();
    setFormErrors(validate(formValues))
    setIsSubmit(true)
  }

  useEffect(()=>{
    // console.log(formErrors)
    if(Object.keys(formErrors).length === 0 && isSubmit){
      console.log(formValues)
    }
  }, [formErrors])

  const validate = (values) => {
    const errors = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;

    if (!values.fullName) {
      errors.fullName = "Username is required!"
    } else if (values.fullName.length < 3) {
      errors.fullName = "Name should be minimum 3 character."
    }

    if (!values.email) {
      errors.email = "Email is required!"
    }else if(!regex.test(values.email)){
      errors.email = "This is not a valid email."
    }
    return errors;
  }

  return (
    <>
      <div className='d-flex align-items-center justify-content-between'>
        <h1>Employee List</h1>
        <Button variant="contained" onClick={()=>addEmployeeBtn()}>Add Employee</Button>
        <DialogBox
          maxWidth="md" 
          title={'Add Employee'} 
          openModal={addEmp} 
          closeModel={addEmployeeBtn} 
          onSubmit={handleSubmit}
          modelData={
            <EmployeeForm 
            cities={cities}
            skills={skills}
            errors={formErrors}
            formValues={formValues}
            handleChange={handleChange} 
            // handleBlur={handleBlur}
            />
          } 
        />
      </div>
    </>
  )
}

export default Employee









