import React, { useState } from 'react'
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import ProfileCard from '../../genericComponent/profile-card/ProfileCard';
import { IconButton, Stack } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import styles from'../employee.module.scss'

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

const EmployeeList = (props) => {
  const { data } = props;
  // const localStore = localStorage.getItem('empList');
  // const empData = JSON.parse(localStore)
  const [empData, setEmpData] = useState(JSON.parse(localStorage.getItem('empList') || ''));


  const handleDelete = (id) => {
    console.log(id)
    const indexValue = empData?.findIndex((data)=>(data.id === id))
    empData.splice(indexValue, 1)
    setEmpData(empData)
    console.log("ðŸš€ ~ file: index.js:32 ~ handleDelete ~ empData:", empData)
    // localStorage.setItem('empList', JSON.stringify(empData))
  }

  return (
    <>
    <Box sx={{ flexGrow: 1 }}>
      <Grid container spacing={2}>
        {data?.map((value, index) => (
          <Grid item xs={4} key={value.id}>
            <Item style={{padding: '0', borderRadius: '0', boxShadow: 'none', height: '100%', display: 'grid'}}>
              <ProfileCard data={value}>
                <Stack direction="row" spacing={1} className={styles.iconPosition}>
                  <IconButton aria-label="delete" color="secondary" size="small" onClick={()=>handleDelete(value.id)}>
                    <DeleteIcon fontSize="inherit" />
                  </IconButton>
                  <IconButton aria-label="delete" color="primary" size="small"  sx={{ ml: 2 }}>
                    <EditIcon fontSize="inherit" />
                  </IconButton>
                </Stack>
              </ProfileCard>
            </Item>
          </Grid>
        ))}
      </Grid>
    </Box>
    </>
  )
}

export default EmployeeList
