import React, { useState } from 'react';
import shortid from 'shortid';
import MessageList from '../MessageList';

const Form = () => {
    const [inputValue, setInputValue] = useState('');
    const [formValid, setFormValid] = useState(false);
    const [todoList, setTodoList] = useState([]);
    // const [error, setError] = useState(false);

    const success = {
        color: 'green'
    }

    const handelOnChange = (event) => {
        setInputValue(event.target.value)
        // console.log(inputValue.length)
        // inputValue !== '' && console.log(inputValue.length)
    }

    const handelSubmit = (event) => {
        event.preventDefault()
        if (inputValue.length > 2) {
            console.log("YES, you can save")
            setInputValue('')
            setTodoList([...todoList, {id:shortid.generate(), message: inputValue}])
            setFormValid(false)
        } else {
            console.log("SORRY")
            setFormValid(true)
        }
    }

    return(
        <>
            <form>
                <input 
                type={'text'} 
                value={inputValue} 
                placeholder={'Add todo'} 
                onChange={handelOnChange}
                />
                <button onClick={handelSubmit}>Add todo</button>
                <br />
                {formValid && <small style={success}>Please enter value</small>}
            </form>
            <MessageList todoList={todoList} />
        </>
    )
}

export default Form
